// Layouts.js

import React, { useState, useEffect } from 'react';
import Topnav from './Topnav'; 
import SideNav from './SideNav';
import Footer from './Footer';

function Layouts({ children }) {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [sidebarHeight, setSidebarHeight] = useState(0);

  const receiveDataFromSideNav = (data) => {
    setIsSidebarOpen(data);
  };

  useEffect(() => {
    // Get the height of the sidebar dynamically
    const sidebar = document.getElementById('sidebar');
    if (sidebar) {
      setSidebarHeight(sidebar.clientHeight);
    }
  }, [isSidebarOpen]);

  return (
    <div className="flex flex-col min-h-screen">
      {/* Topnav */}
      <Topnav />

      {/* Main Content */}
      <div className="flex flex-row">
        {/* SideNav */}
        <div id="sidebar" className={`lg:w-1/5 ${isSidebarOpen ? 'open' : 'close'} fixed h-full overflow-y-auto`}>
          <SideNav sendDataToLayout={receiveDataFromSideNav} />
        </div>

        {/* Main Content */}
        <div className="" style={{ paddingBottom: sidebarHeight }}>
          {children}
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default Layouts;
