import React, { useState } from 'react';
import Topnav from './Topnav'; 
import SideNav from './SideNav';
import Footer from './Footer';

function Layouts({ children }) {
  const [isSideNavOpen, setIsSideNavOpen] = useState(true);

  const toggleSideNav = () => {
    setIsSideNavOpen(!isSideNavOpen);
  };

  return (
    <div className="flex flex-col min-h-screen">
      {/* Topnav */}
      <Topnav />

      {/* Main Content */}
      <div className="flex flex-row">
        {/* SideNav */}
        <div className={`max-w-1/5 md:w-1/5 lg:w-1/5 ${isSideNavOpen ? '' : ''}`}>
          <SideNav />
        </div>

        {/* Main Content */}
        <div className={`flex-1 ml-0 md:ml-1/5 lg:ml-1/5 transition-all ease-in-out duration-300 ${isSideNavOpen ? '' : 'ml-0'}`}>
          {children}
        </div>
      </div>

      {/* Footer */}
      <Footer />

      {/* Toggle button for SideNav */}
      <button
        onClick={toggleSideNav}
        className="fixed bottom-4 right-4 bg-blue-500 text-white px-4 py-2 rounded-full"
      >
        {isSideNavOpen ? 'Close' : 'Open'}
      </button>
    </div>
  );
}

export default Layouts;
