    <div className='flex flex-col lg:flex-row bg-bgBlue justify-end h-screen'>

        <div className='w-fit h-screen p-5 text-black bg-textColor1 w-full md:w-full lg:w-4/12 flex flex-col items-center'>
            <div className='text-3xl font-semibold'>Welcome to Galgotias University</div>
            <div className='place-self-center'>
            <form className="flex flex-col gap-2 border upCard p-2 rounded-lg "ref={loginFrom} onSubmit={handleSubmit} >
               <label>User-ID</label>
               <input type='text' placeholder='StudentID' required className='border rounded-sm p-1 w-full'/> 
