import React, { useEffect, useState } from 'react'
import Layouts from '../../Layouts/Layouts'
import axios from 'axios'
import { PlusCircle } from 'lucide-react';

function MyProject(props) {
   const [projectData,setprojectData] = useState();
   const userData = sessionStorage.getItem('sessionData');
   const[isClicked,setIsClicked] = useState(false);
   const [selectedAdmissionNumber, setSelectedAdmissionNumber] = useState('');
  const [title,setTitle]=useState("");
  const[abstract ,setAbstract] = useState("");

  const[addClick ,setaddClick] = useState();
   const handleSelectChange = (event) => {
     setSelectedAdmissionNumber(event.target.value);
   };
   console.log(selectedAdmissionNumber);
   let admissionNumber;
   if(userData){
     admissionNumber =JSON.parse(userData);
   }
  const [studentData,setStudentData] = useState();
    useEffect(()=>{
        const getProjectData =async()=>{
            const response =await axios.get(`http://localhost:3001/projects/pdSpecific/${admissionNumber.data[0].AdmissionNumber}`);
            setprojectData(response.data.data);
        }
        const getStudentData = async()=>{
            const response = await axios.get('http://localhost:3001/s/studentNotRegistered');
            setStudentData(response.data.data);
        }
        getProjectData();
        getStudentData();
    },[])

    const handleAddMember = async () => {

        try {
           // Replace with the actual admission number

          const response = await axios.post(`http://localhost:3001/projects/addStudnets/${projectData[0].ProjectID}`, {
            admissionNumber: selectedAdmissionNumber,
            user:admissionNumber.data[0].Name,
          }, {
            headers: {
              'Content-Type': 'application/json',
            },
          });

          // Handle the response as needed
          console.log(response.data);
        } catch (error) {
          // Handle errors
          console.error('Error adding member:', error);
        }
      };
      const handleClick=()=>{
        setIsClicked(true);

      }
      const handleAddTitle =()=>{
          setaddClick(1);
      }
    const renderCards = () => {
        const numberOfCards = projectData && projectData[0] && projectData[0].ProjectNumber;

        if (!numberOfCards) {
          return null; // Return null if the number of cards is not available
        }

        const cards = [];
        for (let i = 0; i < numberOfCards; i++) {


          const cardContent = i < projectData.length ? (
            <div className='flex flex-col gap-2'>
              <div>Admission Number: {projectData[i].AdmissionNumber}</div>
              <div>Name: {projectData[i].Name}</div>
              <div>Email: {projectData[i].Email}</div>
              <div>Phone: {projectData[i].Phone}</div>

              <div>Branch: {projectData[i].Branch}</div>

              <div>Year: {projectData[i].Year}</div>

              <div>Semester: {projectData[i].Semester}</div>
              <div>Registered by: {projectData[i].Addby}</div>

            </div>
          ) : (
            <p>
                {!isClicked&&<button onClick={handleClick}><PlusCircle size={100} strokeWidth={1}/>Add member</button>}
                {isClicked&&<div>
             <select value={selectedAdmissionNumber} onChange={handleSelectChange}>
                <option value='' disabled>Select Admission Number</option>
                {studentData.map((student) => (
                <option key={student.AdmissionNumber} value={student.AdmissionNumber}>
                    {`${student.AdmissionNumber} - ${student.Name}`}
                </option>
                ))}
            </select>
                    <button onClick={handleAddMember}>Add member</button>
                    </div>}

            </p>
          );

          cards.push(
            <div key={i}  className="p-10 bg-white upCard border  text-black w-fit flex justify-center items-center">
              {cardContent}
            </div>
          );
        }

        return cards;
      };


    console.log(projectData);
  return (
    <Layouts>
    <div>
            <div className='text-center bg-bgBlueDark text-white text-3xl rounded-2xl p-2 relative'>
              <h1>{projectData&&projectData[0].ProjectID}</h1>
                <div className={`absolute top-2 right-2 flex justify-center items-center gap-2 ${projectData&&projectData[0].Status==='Pending'?'bg-red-500':'bg-green-500'} p-1 rounded-xl pl-2 pr-2 pb-2 text-white`}><div className={`bg-white rounded-full `} style={{width:'10px',height:'10px'}}></div><p className='text-base'>{projectData&&projectData[0].Status}</p></div>
              </div>
           <div className='flex w-full justify-around gap-20 mt-10'>{renderCards()}</div>
           <div>
           <div className='flex m-10 justify-center items-center'>
                  <div className='flex-1'>
                    <div style={{ height: '2px' }} className='bg-gray-300'></div>
                  </div>

                  <div className='flex-0 ml-4 mr-4 font-bold'>
                    <h2>Project Details</h2>
                  </div>

                  <div className='flex-1'>
                    <div style={{ height: '2px' }} className='bg-gray-300'></div>
                  </div>
                </div>

             <div className='flex flex-col md:flex-row lg:flex row justify-around items center'>
              <button className='w-fit p-2 bg-bgBlueDark text-white rounded-xl' onClick={handleAddTitle}>Add title and Abstract</button>
              <button className='w-fit p-2 bg-bgBlueDark text-white rounded-xl'>Add PPT</button>
              <button className='w-fit p-2 bg-bgBlueDark text-white rounded-xl'>Add Project Report</button>
              <button className='w-fit p-2 bg-bgBlueDark text-white rounded-xl'> Project Link</button>
             </div>
             </div>

             {addClick===1&&<div className='w-full flex justify-center items-center mt-5  transition-all ease-in-out duration-300'>
              <div className='bg-white upCard border flex w-5/6 flex-col p-5 gap-2'>
                <label for='title'>Project Title:</label>
                <input className='outline-none border p-2 '  type='text' name='title' value={title} onChange={(e)=>setTitle(e.target.value)} placeholder='Project Title' />
                <label for='abstract' className='mt-2'>
                    Write Abstract for your Project <small>(max 200 words)</small>
                </label>
                <textarea  className='outline-none border p-2' rows={5} name='abstract' value={abstract} onChange={(e)=>{setAbstract(e.target.value)}} />
              </div>
             </div>}
    </div>

    </Layouts>
  )
}

export default MyProject
