import React, { useRef, useState } from 'react';
import Select from 'react-select';

function Login() {
  const loginFrom = useRef();
  const [user, setUser] = useState({
    userId: "",
    password: "",
    role: ""
  });
  const options = [
    { value: 'Admin', label: 'Admin' },
    { value: 'Teacher', label: 'Professor' },
    { value: 'Student', label: 'Student' }
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUser((prev) => ({
      ...prev,
      [name]: value
    }));
  };
  const handleRoleChange = (selectedOption) => {
    // Update the user state when the role is selected
    setUser((prevUser) => ({
      ...prevUser,
      role: selectedOption.value
    }));
  };
console.log(user);
  return (
    <div className="flex flex-col lg:flex-row bg-bgBlue justify-end h-screen">
      <div style={{height:'95vh'}} className="w-full my-5 p-5 mx-4 text-black bg-textColor1 lg:w-4/12 flex flex-col items-center justify-between">
        <div className="  text-xl md:text-2xl lg:text-3xl font-semibold mb-4">Welcome to Galgotias University</div>
        <form className="border upCard p-4 rounded-lg w-full" ref={loginFrom} onSubmit={handleSubmit}>
          <label htmlFor="userId" className="block mb-2">
            User-ID
          </label>
          <input
            type="text"
            id="userId"
            name="userId"
            placeholder="StudentID"
            onChange={handleInputChange}
            value={user.userId}
            required
            className="border rounded-sm p-2 w-full outline-none"
          />
          <label htmlFor="password" className="block mb-2 mt-4">
            Password
          </label>
          <input
            type="password"
            id="password"
            name="password"
            placeholder="********"
            onChange={handleInputChange}
            value={user.password}
            required
            className="border rounded-sm p-2 w-full outline-none"
          />
          <Select options={options} onChange={handleRoleChange} placeholder="Identity" className='mt-4'/>
          <button type="submit" className="bg-bgBlueDark text-white rounded-full px-6 text-center py-2 mt-4">
            Login
          </button>
        </form>
        <div className='copyright'> &copy; 2024 to rycienTech</div>
      </div>
    </div>
  );
}

export default Login;
