import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './Pages/Home';
import Login from './Pages/Login';

const isAuthenticated = () => {
  // Check if userId is present in the session
  const loginStaus = localStorage.getItem('isLogedIn');
  return loginStaus;
};
useEffect(() => {
  // Set a timer for one minute to automatically log out
  const logoutTimer = setTimeout(() => {
    localStorage.setItem('isLogedIn', 'false');
  }, 60000); // 60,000 milliseconds = 1 minute

  // Clean up the timer on component unmount
  return () => clearTimeout(logoutTimer);
}, []);
const PrivateRoute = ({ element, ...rest }) => {
  return isAuthenticated() ? (
    element
  ) : (
    <Navigate to="/" replace /> // Redirect to login page if not authenticated
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route
          path="/Home"
          element={<PrivateRoute element={<Home />} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
