          return null; // Return null if the number of cards is not available
        }

        // const cards = [];
        for (let i = 0; i < numberOfCards; i++) {
          const cardStyle = {
            backgroundColor: i < projectData.length ? 'red' : 'black',
            color: 'white', // Set text color for better visibility
          };

          const cardContent = i < projectData.length ? (
            <div>
              Data: {projectData[i].YourDataProperty}
              {/* Add more data properties as needed */}
            </div>
          ) : (
            <p>Black Box Message</p>
          );

          cards.push(
            <div key={i} style={cardStyle} className="flex felx-row justify-between">
              {cardContent}
            </div>
          );
        }

        return cards;
      };


