    <Layouts>
           <div>
            <div className='text-center bg-bgBlueDark text-white text-3xl rounded-2xl p-2'><h1>{projectData&&projectData[0].ProjectID}</h1></div>
           <div></div>
           }
           </div>
    </Layouts>
  )
}

