  }

  return (
    <div className="h-auto min-h-screen flex flex-col md:flex-row items-center justify-center bg-color1 text-black">
      <StudentNav />
      <div className="w-full max-w-5xl p-6 mt-20 rounded bg-white md:flex md:flex-wrap">
