import React, { useState, useEffect } from "react";
import Select from "react-select";
import axios from "axios";
import { useEmailValidateState } from "../StateManagements/EmailValidState";
const StudentRegistration = ({
  onNumberOfMemberSelection,
  onprojectIdGeneration,
  studentDetails1,
}) => {
  const {projectId,setProjectId} = useEmailValidateState();
  const [member, setMember] = useState(0);
  const [generatedProjectID, setGeneratedProjectID] = useStazte("");
  const [isclicked, setIsClicked] = useState(false);
  const [selectedYear, setSelectedYear] = useState(0);
  const [lastprojectId, setLastProjectID] = useState("");
  const [selectedSemester, setSelectedSemester] = useState(0);
  const optionsYear = [
    { value: 1, label: "1" },
    { value: 2, label: "2" },
    { value: 3, label: "3" },
    { value: 4, label: "4" },
  ];
  const optionsSemester = [
    { value: 1, label: "1" },
    { value: 2, label: "2" },
    { value: 3, label: "3" },
    { value: 4, label: "4" },
    { value: 5, label: "5" },
    { value: 6, label: "6" },
    { value: 7, label: "7" },
    { value: 8, label: "8" },
  ];
  const handleButtonClick = (numberOfMembers) => {
    setMember(numberOfMembers);
    onNumberOfMemberSelection(numberOfMembers);
  };

  const handleClick = async () => {
    setIsClicked(true);

    try {
      const response = await axios.get(
        `http://localhost:3001/createProjectIdTable/${projectId}`
      );
      console.log(response);
    } catch (error) {
      console.error("Error fetching project data:", error);
      // Handle error as needed
    }
  };

  useEffect(() => {
    // This useEffect will run whenever selectedYear or selectedSemester changes
    let visibleSemesters = [];
    let isYearSelected = false;

    // Check if year is selected
    if (selectedYear > 0) {
      isYearSelected = true;

      // Set visible semesters based on the selected year
      if (selectedYear === 1) {
        visibleSemesters = [1, 2];
      } else if (selectedYear === 2) {
        visibleSemesters = [3, 4];
      } else if (selectedYear === 3) {
        visibleSemesters = [5, 6];
      } else if (selectedYear === 4) {
        visibleSemesters = [7, 8];
      }
    }

    setOptionsSemester(
      visibleSemesters.map((semester) => ({ value: semester, label: semester.toString() }))
    );

    // Reset selected semester when year changes
    if (!isYearSelected) {
      setSelectedSemester(0);
    }

    // Disable "Create Group" button if either year or semester is not selected
    setIsCreateGroupDisabled(!isYearSelected || selectedSemester === 0);

  }, [selectedYear, selectedSemester]);

  useEffect(() => {
    // This useEffect will run whenever lastprojectId changes
    const generateProjectId = async() => {
      try {
        const response = await axios.get(
          `http://localhost:3001/projectsData?year=${selectedYear}&semester=${selectedSemester}`
          );
        setLastProjectID(response.data[0].projectId);
      } catch (error) {
        console.error("Error fetching project data:", error);
        // Handle error as needed
      }
      const code =
        "BT" +
        selectedYear +
        selectedSemester +
        (parseInt(lastprojectId.substring(4, lastprojectId.length)) + 1);
        setProjectId(code);
      onprojectIdGeneration(code);

      // console.log(response);
    };

    generateProjectId();
  }, [lastprojectId, selectedYear, selectedSemester]);

  console.log(lastprojectId);
  console.log(projectId);
  return (
    <div>
      <label className="block text-sm font-semibold mb-2">Select Year:</label>
      <Select
        className="text-black"
        options={optionsYear}
        value={optionsYear.find((option) => option.value === selectedYear)}
        onChange={(selectedOption) => setSelectedYear(selectedOption.value)}
      />
      <label className="block text-sm font-semibold mb-2">
        Select semester:
      </label>
      <Select
        className="text-black"
        options={optionsSemester}
        value={optionsSemester.find(
          (option) => option.value === selectedSemester
        )}
        onChange={(selectedOption) => setSelectedSemester(selectedOption.value)}
      />

      <button onClick={handleClick}>Create Group</button>
      {isclicked && (
        <div>
          <h2>Select the number of members in the group</h2>
          <div className="d-flex gap-5">
            <button
              onClick={() => handleButtonClick(1)}
              className="rounded-full bg-red-200 p-2"
            >
              1
            </button>
            <button
              onClick={() => handleButtonClick(2)}
              className="rounded-full bg-red-200 p-2"
            >
              2
            </button>
            <button
              onClick={() => handleButtonClick(3)}
              className="rounded-full bg-red-200 p-2"
            >
              3
            </button>
            <button
              onClick={() => handleButtonClick(4)}
              className="rounded-full bg-red-200 p-2"
            >
              4
            </button>
            <button
              onClick={() => handleButtonClick(5)}
              className="rounded-full bg-red-200 p-2"
            >
              5
            </button>
          </div>
        </div>
      )}
      <h2> {projectId}</h2>
    </div>
  );
};

export default StudentRegistration;
