// Layouts.js

import React, { useState } from 'react';
import Topnav from './Topnav'; 
import SideNav from './SideNav';
import Footer from './Footer';

function Layouts({ children }) {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const receiveDataFromSideNav = (data) => {
    setIsSidebarOpen(data);
  };

  return (
    <div className="grid grid-cols-12 gap-4 min-h-screen">
    {/* Topnav */}
    <div className="col-span-12">
      <Topnav />
    </div>
      {/* Main Content */}
      <div className={`${isSidebarOpen?'col-sapn-4':'col-span-1'}`}>
        <SideNav sendDataToLayout={receiveDataFromSideNav} />
      </div>

      {/* Main Content */}
      <div className={`p-4 ${isSidebarOpen?'col-span-11 bg-red-700':'col-span-8'}`} >
        {children}
      </div>
      <div className="col-span-12">
        <Footer />
      </div>
    </div>
  );
}

export default Layouts;
